    if match:
        l = match.group(1)
        if l.isdigit() and l.isalpha():
            l = float(l)
            num_units = re.findall(r'\d+', l)
            units = []
            for unit in num_units:
                units.append(int(unit))
            return sum(units)
        else:
            return None
    return None
